import React from "react";
import logo from './logo.svg';
import './App.css';

function App() {
  const [allCanvasNotes, setAllCanvasNotes] = React.useState(null);
  const [name, setName] = React.useState('');
  const [notes, setNotes] = React.useState('');
  const [message, setMessage] = React.useState('');

  React.useEffect(() => {
    fetch("/canvas")
      .then((res) => res.json())
      .then((json) => setAllCanvasNotes(json.data));
  }, []);

  // Override default submit behavior
  // Send a POST API request to create a new canvas record in the database
  // Indicate success or failure with a message back to the user
  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('/canvas', {
      method: "POST",
      headers: {
          'Content-type': 'application/json'
      },
      body: JSON.stringify({
        name: name,
        notes: notes,
      })
    })
    .then((res) => res.json())
    .then((json) => {
      if (json.status === 201) {
        setName('');
        setNotes('');
        setMessage('Notes saved');
      } else {
        setMessage('Error saving notes');
      }
    });
  }

  console.log(allCanvasNotes)

  return (
    <div className="App">
      <h1>Canvassing Notes</h1>
      {allCanvasNotes.map((data, index) => {
        <p>{data.name}</p>
      })}
      <hr></hr>
      <h3>Add Canvassing Notes</h3>
      <div className="canvas-form">
        <form onSubmit={handleSubmit}>
          <input 
            type="text" 
            value={name} 
            placeholder="Name"
            onChange={(e) => setName(e.target.value)} 
          />
          <br/>
          <textarea 
            value={notes} 
            placeholder="Notes"
            onChange={(e) => setNotes(e.target.value)} 
          />
          <br/>
          <button type='submit' >Save Notes</button>
          <div className="message">
            {message ? <p>{message}</p> : null}
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
