import React from "react";
import logo from './logo.svg';
import './App.css';

function App() {
  const [allCanvasNotes, setAllCanvasNotes] = React.useState(null);
  const [name, setName] = React.useState('');
  const [notes, setNotes] = React.useState('');
  const [message, setMessage] = React.useState('');

  React.useEffect(() => {
    fetch("/canvas")
      .then((res) => res.json())
      .then((json) => setAllCanvasNotes(json.data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(name);
    console.log(notes);

    let response = fetch('/canvas', {
      method: "POST",
      headers: {
          'Content-type': 'application/json'
      },
      body: JSON.stringify({
        name: name,
        notes: notes,
      })
    });

    if (response.status === 200) {
      setName('');
      setNotes('');
      setMessage('Notes saved');
    } else {
      setMessage('Error saving notes');
    }
  }

  return (
    // <div className="App">
    //   <header className="App-header">
    //     <img src={logo} className="App-logo" alt="logo" />
    //     <p>{!allCanvasNotes ? "Loading..." : allCanvasNotes}</p>
    //   </header>
    // </div>

    <div className="App">
      <h1>Add Canvas Notes</h1>
      <div className="canvas-form">
        <form onSubmit={handleSubmit}>
          <input 
            type="text" 
            value={name} 
            placeholder="Name"
            onChange={(e) => setName(e.target.value)} 
          />
          <br/>
          <textarea 
            value={notes} 
            placeholder="Notes"
            onChange={(e) => setNotes(e.target.value)} 
          />
          <br/>
          <button type='submit' >Save Notes</button>
          <div className="message">
            {message ? <p>{message}</p> : null}
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
